var Model = require('ampersand-state');
var Decimal = require('big.js');

var decimalType = require('utils/decimal-type');

/* @ngInject */
module.exports = function () {
  return Model.extend({

    idAttribute: 'id',
    typeAttribute: 'modelType',

    props: {
      id: 'number',
    },

    dataTypes: {
      decimal: decimalType,
    },
  });
};
