var Decimal = require('big.js');

module.exports = {
  
  set: function (newVal) {
    var val;
    if (newVal instanceof Decimal) {
      val = newVal;
    } else {
      val = new Decimal(newVal);
    }
    return {
      val: val,
      type: "decimal",
    };
  },

  compare: function (currentVal, newVal, attributeName) {
    return currentVal && currentVal.eq(newVal);
  },

  get: function (val) {
    return val;
  },

  'default': function () {
    return new Decimal(0);
  },
};
