var moment = require('moment');

module.exports = {
  
  set: function (newVal) {
    var val;
    if (moment.isMoment(newVal)) {
      val = newVal;
    } else {
      val = moment(newVal);
    }
    return {
      val: val,
      type: "moment",
    };
  },

  compare: function (currentVal, newVal, attributeName) {
    return currentVal && currentVal.isSame(newVal);
  },

  get: function (val) {
    return val;
  },
};
