var AmpView = require('ampersand-view');
var Modal = require('modal');

module.exports = function (options, callback) {
  console.log("new yes or no modal")
  return Modal({
    title: options.title,
    size: 'lg',
    BodyView: AmpView.extend({
      template: options.body,
      autoRender: true,
    }),
    submitText: "Yes",
    showCancel: true,
    cancelText: "No",
  }, callback);
};
